import React, { Component } from "react";
import RestaurantButton from "./RestaurantButton";

const Order = () => {
  [orders, setOrders] = useState(0);

  orderOne(() => {
    setOrders(orders + 1);
  });

  return (
    <li>
      {this.props.orderType}: {this.state.orders}
      <RestaurantButton fnc={this.orderOne} />
    </li>
  );
};

