import React, { Component } from "react";
import moment from "moment";

class SearchResults extends Component {
  constructor() {
    super();
    this.state = {
      selected: false,
    };
    this.selectRow = this.selectRow.bind(this);
  }

  selectRow() {
    const row = e.currentTarget;
    console.log(row);
    this.setState((prevState) => {
      return {
        selected: !prevState.selected,
      };
    });
  }

  render() {
    let bookingAttributes = Object.keys(this.props.results[0]);
    return (
      <table className="table">
        <thead className="thead-dark">
          <tr>
            {bookingAttributes.map((att, index) => {
              return (
                <th scope="col" key={index}>
                  {att}
                </th>
              );
            })}
            <th scope="col" key="9">
              nights
            </th>
          </tr>
        </thead>
        <tbody>
          {this.props.results.map((att, index) => {
            let checkIn = moment().format(att.checkInDate);
            let formattedCheckIn = moment(checkIn);
            let checkOut = moment().format(att.checkOutDate);
            let formattedCheckOut = moment(checkOut);
            let nights = formattedCheckOut.diff(formattedCheckIn, "days");
            return (
              <tr
                onClick={this.selectRow}
                key={index}
                className={this.state.selected ? "highlight" : ""}
              >
                <td>{att.id}</td>
                <td>{att.title}</td>
                <td>{att.firstName}</td>
                <td>{att.surname}</td>
                <td>{att.email}</td>
                <td>{att.roomId}</td>
                <td>{att.checkInDate}</td>
                <td>{att.checkOutDate}</td>
                <td>{nights}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  }
}

export default SearchResults;
