import React, { Component } from "react";
import moment from "moment";

const SearchResults = () => {
  selectRow((e) => {
    const row = e.currentTarget;
    row.className = row.className === "" ? "highlight" : "";
  });

  return (
    <table className="table">
      <thead className="thead-dark">
        <tr>
          {bookingAttributes.map((att, index) => {
            return (
              <th scope="col" key={index}>
                {att}
              </th>
            );
          })}
          <th scope="col" key="9">
            nights
          </th>
        </tr>
      </thead>
      <tbody>
        {this.props.results.map((att, index) => {
          let checkIn = moment().format(att.checkInDate);
          let formattedCheckIn = moment(checkIn);
          let checkOut = moment().format(att.checkOutDate);
          let formattedCheckOut = moment(checkOut);
          let nights = formattedCheckOut.diff(formattedCheckIn, "days");
          return (
            <tr onClick={this.selectRow} key={index} className={""}>
              <td>{att.id}</td>
              <td>{att.title}</td>
              <td>{att.firstName}</td>
              <td>{att.surname}</td>
              <td>{att.email}</td>
              <td>{att.roomId}</td>
              <td>{att.checkInDate}</td>
              <td>{att.checkOutDate}</td>
              <td>{nights}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

