import React, { Component } from "react";
import RestaurantButton from "./RestaurantButton";

const Order = (props) => {
  const [orders, setOrders] = useState(0);

  function orderOne() {
    setOrders(orders + 1);
  }

  return (
    <li>
      {props.orderType}: {state.orders}
      <RestaurantButton fnc={orderOne} />
    </li>
  );
};

export default Order;
