import React from "react";
import moment from "moment";

const SearchResults = (props) => {
  function selectRow(e) {
    const row = e.currentTarget;
    row.className = row.className === "" ? "highlight" : "";
  }

  let bookingAttributes = Object.keys(props.results[0]);

  return (
    <table className="table">
      <thead className="thead-dark">
        <tr>
          <th scope="col" key="3">
            nights
          </th>
          <th scope="col" key="4">
            nights
          </th>
          <th scope="col" key="5">
            nights
          </th>
          <th scope="col" key="6">
            nights
          </th>
          <th scope="col" key="7">
            nights
          </th>
          <th scope="col" key="8">
            nights
          </th>
          <th scope="col" key="9">
            nights
          </th>
          <th scope="col" key="10"></th>
        </tr>
      </thead>
      <tbody>
        {props.results.map((att, index) => {
          let checkIn = moment().format(att.checkInDate);
          let formattedCheckIn = moment(checkIn);
          let checkOut = moment().format(att.checkOutDate);
          let formattedCheckOut = moment(checkOut);
          let nights = formattedCheckOut.diff(formattedCheckIn, "days");
          return (
            <tr onClick={selectRow} key={index} className={""}>
              <td>{att.id}</td>
              <td>{att.title}</td>
              <td>{att.firstName}</td>
              <td>{att.surname}</td>
              <td>{att.email}</td>
              <td>{att.roomId}</td>
              <td>{att.checkInDate}</td>
              <td>{att.checkOutDate}</td>
              <td>{nights}</td>
              <td>
                <button>Show Profile</button>
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

